age = int(input('Enter your age: '))
if age < 13:
    print('You are a child')
elif age <= 17:
    print('You are a teenager')
else:
    print('You are an adult')

score = int(input('Enter your test score: '))
if score >= 90:
    print('Grade: A')
elif score >= 80:
    print('Grade: B')
elif score >= 70:
    print('Grade: C')
elif score >= 60:
    print('Grade: D')
else:
    print('Grade: F')

