number = int(input('Enter a number: '))
if number % 2 == 0:
    print(f'{number} is even')
else:
    print(f'{number} is odd')

temp = int(input('Enter temperature in Fahrenheit: '))
if temp < 60:
    print('Wear a coat')
elif temp <= 75:
    print('Wear a sweater')
else:
    print('Wear light clothes')
